import React, { useState, useEffect } from 'react';
import { FlatList, Text, TouchableOpacity, View, ActivityIndicator } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { getPokemonList } from '../api/pokeAPI';
import { StackNavigationProp } from '@react-navigation/stack';
import { RootStackParamList } from '../def';


// DÃ©finissez le type de navigation pour votre Stack
type HomeScreenNavigationProp = StackNavigationProp<
  RootStackParamList,
  'HomeScreen'
>;

const HomeScreen = () => {
  const [loading, setLoading] = useState(true);
  const [pokemons, setPokemons] = useState([]);

  // Utilisez le type de navigation ici
  const navigation = useNavigation<HomeScreenNavigationProp>();

  useEffect(() => {
    const fetchPokemon = async () => {
      setLoading(true);
      try {
        const data = await getPokemonList();
        setPokemons(data.results);
      } catch (error) {
        console.error(error);
      } finally {
        setLoading(false);
      }
    };

    fetchPokemon();
  }, []);

  const renderPokemon = ({ item }) => (
    <TouchableOpacity 
      style={styles.itemContainer}
      onPress={() => navigation.navigate('DetailScreen', { name: item.name })}
    >
      <View style={styles.sprite}></View> 
      {/* Remplacez par une image quand vous avez les sprites disponibles */}
      <Text style={styles.nameText}>Number : {item.name}</Text>
    </TouchableOpacity>
  );

  if (loading) {
    return <ActivityIndicator size="large" color="#0000ff" />;
  }

  return (
    <View>
      <FlatList
        data={pokemons}
        renderItem={renderPokemon}
        keyExtractor={item => item.name}
      />
    </View>
  );
};

export default HomeScreen;
