import { createSlice, PayloadAction, createAsyncThunk } from '@reduxjs/toolkit';
import { getPokemonList, getPokemonByName } from '../api/pokeAPI';

interface PokemonState {
    list: any[];
    current: any | null;
    loading: boolean;
    error: string | null;
}

const initialState: PokemonState = {
    list: [],
    current: null,
    loading: false,
    error: null
};

// Actions asynchrones
export const fetchPokemonList = createAsyncThunk('pokemon/fetchList', async () => {
    const response = await getPokemonList();
    return response.results;
});

export const fetchPokemonByName = createAsyncThunk('pokemon/fetchByName', async (name: string) => {
    const response = await getPokemonByName(name);
    return response;
});

const pokemonSlice = createSlice({
    name: 'pokemon',
    initialState,
    reducers: {},
    extraReducers: builder => {
        builder
            .addCase(fetchPokemonList.rejected, (state, action) => {
                state.error = action.error.message;
                state.loading = false;
            })
            .addCase(fetchPokemonByName.rejected, (state, action) => {
                state.error = action.error.message;
                state.loading = false;
            });
    }
});

export default pokemonSlice.reducer;
